console.clear();const $form=document.querySelector("form"),$input=document.querySelector("textarea"),$button=document.querySelector("button"),$phpOutput=document.querySelector(".php-output"),$output=document.querySelector("output");var wordCount=0;function addClass(t,o){t.classList.add(o)}function createWordArray(t){return t.split(" ")}function countWords(t){return t.length}function handleForm(){var t=createWordArray($input.value);wordCount=countWords(t),$output.innerHTML=`\n\t\t<p class="small-voice">${wordCount} words</p>\n\t\t<p class="small-voice">PS - we don't actually take complaints here. But let it out!</p>\n\t`,1===wordCount&&($output.innerHTML='<p class="small-voice">1 word</p>')}addClass($button,"hiddenByJS"),addClass($phpOutput,"hiddenByJS"),$output.innerHTML=`<p class="small-voice">${wordCount} words</p>`,$form.addEventListener("submit",(function(t){t.preventDefault()})),$input.addEventListener("input",(function(){handleForm()}));