import{palettes,body}from"./../../main.js";palettes.addEventListener("click",(function(t){if(t.target.matches('input[type="radio"]')){const e=document.querySelector(".palettes .active");let a=t.target.dataset.hue;document.cookie=`themeCode=${a}`,body.style.setProperty("--hue",a),e.classList.remove("active"),t.target.closest(".colorSwatch").classList.add("active")}}));