const bioControls=document.querySelector("biography-controls form"),bioOutlet=document.querySelector("biography-card");function renderBio(t){return`\n\t\t<picture>\n\t\t\t<img src="images/${t.portrait}">\n\t\t</picture>\n\t\t<ul class="medium-voice bio-attributes">\n\t\t\t<li>\n\t\t\t\t<h3><strong class="bold">Type: </strong>${t.type}</h3>\n\t\t\t</li>\n\t\t\t<li>\n\t\t\t\t<h3><strong class="bold">Rarity: </strong>${t.rarity}</h3>\n\t\t\t</li>\n\t\t\t<li>\n\t\t\t\t<h3><strong class="bold">Interests: </strong>${t.interests}</h3>\n\t\t\t</li>\n\t\t</ul>\n\n\t\t<text-content>\n\t\t\t<h3 class="large-voice"><strong>Biography:</strong></h3>\n\n\t\t\t<p>${t.bioParagraph}</p>\n\n\t\t\t<p>${t.closingSentence}</p>\n\t\t</text-content>\n\n\t\t<button>Check out my work</button>\n\t`}bioControls.addEventListener("change",(function(t){if("radio"===t.target.type){let n=t.target.id;bioOutlet.innerHTML=n}}));