import{database,palettes,body}from"./../../main.js";palettes.addEventListener("click",(function(e){if(e.target.matches(".color")){const t=document.querySelector(".palettes .active");let a=Number(e.target.id);database.setItem("themeCode",a),body.style.setProperty("--hue",a),e.target.classList.add("active"),t.classList.remove("active")}}));