const button=document.querySelector(".menu-hamburger"),nav=document.querySelector(".site-nav"),exitMenuButton=document.querySelector(".exit-menu"),body=document.querySelector("body");let menuOpen=!1;function toggleMenu(){menuOpen=!menuOpen}function updateMenuClasses(){!0===menuOpen?(nav.classList.add("open"),nav.classList.remove("closed"),body.classList.add("noScroll")):(nav.classList.remove("open"),nav.classList.add("closed"),body.classList.remove("noScroll"))}function closeMenu(){menuOpen=!1,body.classList.remove("noScroll"),updateMenuClasses()}nav.classList.add("closed"),button.addEventListener("click",(function(){toggleMenu(),updateMenuClasses()})),exitMenuButton.addEventListener("click",(function(){closeMenu()})),window.addEventListener("resize",(function(){window.innerWidth>=850&&closeMenu()}));