const button=document.querySelector(".menu-hamburger"),nav=document.querySelector(".site-nav"),exitMenuButton=document.querySelector(".exit-menu");let menuOpen=!1;function toggleMenu(){menuOpen=!menuOpen}function updateNavClass(){!0===menuOpen?(nav.classList.add("open"),nav.classList.remove("closed")):(nav.classList.remove("open"),nav.classList.add("closed"))}function closeMenu(){menuOpen=!1,updateNavClass()}nav.classList.add("closed"),button.addEventListener("click",(function(){toggleMenu(),updateNavClass()})),exitMenuButton.addEventListener("click",(function(){closeMenu()})),window.addEventListener("resize",(function(){window.innerWidth>=850&&closeMenu()}));