console.clear();var $form=document.querySelector("form"),lengthInput=document.querySelector("#length"),widthInput=document.querySelector("#width"),$output=document.querySelector("#form-result");const CONVERSION_FACTOR=.09290304;function calculateFeet(e,t){return parseInt(e)*parseInt(t)}function calculateMeters(e){return Math.round(100*(e*CONVERSION_FACTOR+Number.EPSILON))/100}function renderResults(e,t,u=document.body){u.innerHTML=`<p class='form-result'>You room is ${e.toLocaleString()} square feet and ${t.toLocaleString()} square meters.</p>`}function handleForm(){var e=calculateFeet(lengthInput.value,widthInput.value);renderResults(e,calculateMeters(e),$output)}$form.addEventListener("submit",(function(e){e.preventDefault(),lengthInput.value&&widthInput.value?handleForm():alert("Please submit two numbers")}));