console.clear();var $form=document.querySelector("form"),$inputOne=document.querySelector("#firstNumber"),$inputTwo=document.querySelector("#secondNumber"),$output=document.querySelector("#form-result");function calculateResults(e,t){return[{name:"Sum",operation:e+" + "+t,result:e+t},{name:"Diff",operation:e+" - "+t,result:e-t},{name:"Product",operation:e+" * "+t,result:e*t},{name:"Quotient",operation:e+" / "+t,result:Math.round(100*(e/t+Number.EPSILON))/100}]}function renderResults(e,t=document.body){var u='<ul class="form-result">';e.forEach((function(e){u+=`<li>${e.operation} = ${e.result}</li>`})),u+="</ul>",t.innerHTML=u}function handleForm(){renderResults(calculateResults($inputOne.value,$inputTwo.value),$output)}$form.addEventListener("submit",(function(e){e.preventDefault(),$inputOne.value&&$inputTwo.value?handleForm():alert("Please submit two numbers")}));