console.clear();var $form=document.querySelector("form"),$inputOne=document.querySelector("#firstNumber"),$inputTwo=document.querySelector("#secondNumber"),$output=document.querySelector("#form-result");$form.addEventListener("submit",(function(e){if(e.preventDefault(),$inputOne.value&&$inputTwo.value){var u=Number($inputOne.value),t=Number($inputTwo.value),r=[{name:"Sum",operation:u+" + "+t,result:u+t},{name:"Diff",operation:u+" - "+t,result:u-t},{name:"Product",operation:u+" * "+t,result:u*t},{name:"Quotient",operation:u+" / "+t,result:Math.round(100*(u/t+Number.EPSILON))/100}];console.log(r);var o='<ul class="form-result">';r.forEach((function(e){o+=`<li>${e.operation} = ${e.result}</li>`})),o+="</ul>",$output.innerHTML=o}else alert("Please submit two numbers")}));