export function createElementVar(e){return document.querySelector(`${e}`)}export function renderView(e){$main.innerHTML=templates[e]}export function setCurrentUser(e){database.setItem("currentUser",e.username),$header.innerHTML=`\n\t\t<p class="small-voice currentUser">Account: ${database.getItem("currentUser")}</p>\n\t\t\t`}export function updateUsers(e){let r=JSON.stringify(e);database.setItem("users",r)}export function handleAccountCreation(e){let r=e.querySelector("#username"),t=e.querySelector("#password");const s=r.value,n=t.value;if(s&&n){let e={username:s,password:n};if(!users.find((function(r){return r.username===e.username})))return users=[...users,e],setCurrentUser(e),updateUsers(users),r.value="",t.value="",!0;alert("choose another username")}}export function handleSignIn(e){let r=e.querySelector("#username"),t=e.querySelector("#password");const s=r.value,n=t.value;if(s&&n){let e={username:s,password:n};var a=users.find((function(r){return r.username===e.username}));if(a){if(a.password===e.password)return setCurrentUser(e),alert("success!"),!0;alert("The password you entered does not match the username")}else alert("the username you entered does not exist")}else alert("please enter a username and password")}